<?php
 function ShowGroup() { global $db; $array = array(); $sql = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_admingroup"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $array['g_id'] = $rows['g_id']; $array['g_name'] = $rows['g_name']; $array['g_description'] = $rows['g_description']; $arrays[] = $array; } return $arrays; } function ShowAdminUser() { global $db; $array = array(); $sql = "select a.*,b.g_description from ".DB_DAEMDB.".".TB_SUFFIX."db_adminuser as a left join ".DB_DAEMDB.".".TB_SUFFIX."db_admingroup as b on a.a_gid=b.g_id"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $array['id'] = $rows['a_id']; $array['account'] = $rows['a_account']; $array['department'] = $rows['a_department']; $array['username'] = $rows['a_username']; $array['number'] = $rows['a_number']; $array['tel'] = $rows['a_tel']; $array['logip'] = $rows['a_logip']; $array['logtime'] = date("Y-m-d H:i:s",$rows['a_logtime']); $array['ispermit'] = $rows['a_ispermit']; $array['regtime'] = date("Y-m-d H:i:s",$rows['a_regtime']); $array['g_name'] = $rows['g_description']; $arrays[] = $array; } return $arrays; } function ShowMenu() { global $db; $sql = "select m_id,m_name from ".DB_DAEMDB.".".TB_SUFFIX."db_menu where m_parentid='0'"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $array['id'] = $rows['m_id']; $array['name'] = L($rows['m_name']); $arrays[] = $array; } return $arrays; } function ShowResource() { global $db; $sql = "select r_id,r_label,r_name from ".DB_DAEMDB.".".TB_SUFFIX."db_resource where r_parentid='0'"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { } } function ShowResourceTop() { global $db; $sql = "select r_id,r_name from ".DB_DAEMDB.".".TB_SUFFIX."db_resource where r_parentid='0'"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $array['id'] = $rows['r_id']; $array['name'] = $rows['r_name']; $arrays[] = $array; } return $arrays; } function ShowResourceList($parentid,$string='') { global $db; $sql = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_resource where r_parentid='".$parentid."'"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $string .= '<tr><td bgcolor="#FFFFFF" height="25">'.$rows['r_name'].'&nbsp;（<span style="font-size:14px;color:#ff0000;">'.$rows['r_label'].'</span>）&nbsp;&nbsp;<a href="resource_list.php?id='.$rows['r_id'].'">修改</a>&nbsp;&nbsp;<a href="del.php?id='.$rows['r_id'].'&type=resource" onclick="if(!confirm(\'您真的要删除《'.$rows['r_name'].'》资源吗？删除后将无法恢复。\')){return false;}">删除</a></td></tr>'; $string .= '<tr><td bgcolor="#FFFFFF" height="25"><table width="96%" border="0" cellspacing="1" cellpadding="2"  align="center"><tr>'; $sql1 = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_resource where r_parentid='".$rows['r_id']."'"; $result1 = $db->query($sql1); $i = 0; while ($rows1 = $db->fetch_array($result1)) { $string .= '<td width="33%" height="25">'.$rows1['r_name'].'&nbsp;（<span style="font-size:14px;color:#ff0000;">'.$rows1['r_label'].'</span>）&nbsp;&nbsp;<a href="resource_list.php?id='.$rows1['r_id'].'">修改</a>&nbsp;&nbsp;<a href="del.php?id='.$rows1['r_id'].'&type=resource" onclick="if(!confirm(\'您真的要删除《'.$rows1['r_name'].'》资源吗？删除后将无法恢复。\')){return false;}">删除</a></td>'; $i++; if ($i % 3 == 0) { $string .= '</tr><tr>'; } } $string .= '</tr></table></td></tr>'; } return $string; } function ShowPurview($parentid,$gid,$date) { global $db; $sql = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_menu where m_parentid='".$parentid."'"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $checked = ''; if ($date != '') { foreach ($date as $val) { if ($val['p_rid'] == $rows['m_id']) { $checked = 'checked'; } } } $string .= '<tr><td><input type="checkbox" name="purview[]" value="'.$rows['m_id'].'" '.$checked.' onclick="selectpurview(this)" />&nbsp;<b>'.L($rows['m_name']).'</b></td></tr>'; $string .= '<tr><td><table><tr>'; $sql1 = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_menu where m_parentid='".$rows['m_id']."'"; $result1 = $db->query($sql1); $i = 0; while ($rows1 = $db->fetch_array($result1)) { $checkeds = ''; if ($date != '') { foreach ($date as $val) { if ($val['p_rid'] == $rows1['m_id']) { $checkeds = 'checked'; } } } $string .= '<td><input type="checkbox" name="purview'.$rows['m_id'].'[]" value="'.$rows1['m_id'].'" '.$checkeds.' />&nbsp;'.L($rows1['m_name']).'</td>'; $i++; if ($i % 3 == 0) { $string .= '</tr><tr>'; } } $string .= '</tr><tr><td colspan="3"><input type="checkbox" name="all'.$rows['m_id'].'" id="all'.$rows['m_id'].'" value="" onclick="allselect('.$rows['m_id'].',this)" />&nbsp;全选</td></tr></table></td></tr>'; } return $string; } function ShowPurviewInfo($gid) { global $db; $sql = "select p_rid from ".DB_DAEMDB.".".TB_SUFFIX."db_purview where p_gid='".$gid."'"; $result = $db->query($sql); while ($rows = $db->fetch_array($result)) { $array['p_rid'] = $rows['p_rid']; $arrays[] = $array; } return $arrays; } function get_infinite_classify($parent_id,&$depositAry) { global $db; $sql = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_menu where m_parentid='".$parent_id."' order by m_locality"; $query = $db->query($sql); while ($row = $db->fetch_array($query)) { $depositAry[$row["m_id"]] = get_divide_char($row['level'],2,'-').'<img src="../images/menu_plus.gif">&nbsp;'.L($row["m_name"]); get_infinite_classify($row['m_id'],$depositAry); } } function get_infinite_classify_by_permission($parentid) { global $db; if ($_SESSION['UserGroup'] != 1) { $sql = "select m.* from ".DB_DAEMDB.".".TB_SUFFIX."db_menu m,".DB_DAEMDB.".".TB_SUFFIX."db_purview p
				where m_parentid='".$parentid."' and m_id=p_rid and p_gid='".$_SESSION['UserGroup']."' and m_isview='1' UNION select m.* from ".DB_DAEMDB.".".TB_SUFFIX."db_menu m,".DB_DAEMDB.".".TB_SUFFIX."db_purview p
				where m_isview='2'
				order by m_locality asc"; } else { $sql = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_menu where m_parentid='".$parentid."' and (m_isview='1') or (m_isview='2')order by m_locality asc"; } $query = $db->query($sql); $arr = array(); if($query && mysql_affected_rows()) { while($row = mysql_fetch_assoc($query)) { $row['list'] = get_infinite_classify_by_permission($row['m_id']); $arr[] = $row; } return $arr; } } function get_permission_infinite_classify_sort_by_mid($parentid) { global $db; if ($_SESSION['UserGroup'] != 1) { $sql = "select m.* from ".DB_DAEMDB.".".TB_SUFFIX."db_menu m,".DB_DAEMDB.".".TB_SUFFIX."db_purview p
				where m_parentid='".$parentid."' and m_id=p_rid and p_gid='".$_SESSION['UserGroup']."' and m_isview='1' UNION select m.* from ".DB_DAEMDB.".".TB_SUFFIX."db_menu m,".DB_DAEMDB.".".TB_SUFFIX."db_purview p
				where m_isview='2'
				order by m_locality asc"; } else { $sql = "select * from ".DB_DAEMDB.".".TB_SUFFIX."db_menu where m_parentid='".$parentid."' and (m_isview='1') or (m_isview='2')order by m_locality asc"; } $query = $db->query($sql); $arr = array(); if($query && mysql_affected_rows()) { while($row = mysql_fetch_assoc($query)) { $row['list'] = get_permission_infinite_classify_sort_by_mid($row['m_id']); $arr[$row['m_id']] = $row; } return $arr; } } function get_user_resource($UserGroup) { global $db; $dataAry = array(); $UserGroup = empty($UserGroup) ? $_SESSION['UserGroup'] : $UserGroup; $sql = "select p_rid from ".DB_DAEMDB.".".TB_SUFFIX."db_purview where p_gid='".$UserGroup."'"; $query = $db->query($sql); while ($row = $db->fetch_array($query)) { $dataAry['p_rid'] = 1; } return $dataAry; } function get_user_module_sign_list($UserGroup) { global $db; $dataAry = array(); $UserGroup = empty($UserGroup) ? $_SESSION['UserGroup'] : $UserGroup; $sql = "select module_sign from ".DB_DAEMDB.".".TB_SUFFIX."db_purview a, ".DB_DAEMDB.".".TB_SUFFIX."db_menu b
			where a.p_rid = b.m_id and p_gid='".$UserGroup."'"; $query = $db->query($sql); while ($row = $db->fetch_array($query)) { $dataAry[$row['module_sign']] = 1; } return $dataAry; } function get_visit_module() { global $db; $uriVisit = get_visit_uri(); if($uriVisit != 'product/catalog.php') { $sql = "select module_sign from ".DB_DAEMDB.".".TB_SUFFIX."db_menu
				where m_url='".$uriVisit."'"; $result = $db->query_first($sql); $moduleSign = $result['module_sign']; } else { $paramAry = get_uri_param_array(); $catalog = $paramAry['catalog']; $sql = "select module_sign from ".DB_DAEMDB.".".TB_SUFFIX."db_menu
				where m_name='".urldecode($catalog)."'"; $result = $db->query_first($sql); $moduleSign = $result['module_sign']; } return $moduleSign; } function authority_permission_backup($UserGroup) { if($UserGroup == '1') return true; else { $tmpAry = explode('/',DAEM_SELF); $seed = count($tmpAry)-1; $tmpStr = str_replace(array('.html','.htm','.php'),'',$tmpAry[$seed]); $allowModuleList = get_user_module_sign_list($UserGroup); if (isset($allowModuleList[$UserGroup])) { return TRUE; } else { gourl('',DAEM_PATH.'admin/error.php'); } } } function authority_permission($UserGroup) { global $db; if($UserGroup == '1') return true; else { $url = $_SERVER['REQUEST_URI']; $url = str_replace('/DRS/admin/','',$url); $sql = "select module_sign, length(replace(m_url,'".$url."','')) from db_menu where  m_url like '".$url."%'  order by length(replace(m_url,'".$url."',''))"; $result = $db->query_first($sql); $moduleSign = $result["module_sign"]; $allowModuleList = get_user_module_sign_list($UserGroup); if (isset($allowModuleList[$moduleSign]) || $moduleSign == '') { return TRUE; } else { gourl('',DAEM_PATH.'admin/error.php'); } } }